function CreateCS571() {
	let overridenBadgerId;

	function setBadgerId(id) {
		overridenBadgerId = id;
	}
	
	function getBadgerId() {
		const bid = overridenBadgerId ? overridenBadgerId : process.env['EXPO_PUBLIC_CS571_BADGER_ID'];
		if (!bid) {
			console.error("No environment variable 'EXPO_PUBLIC_CS571_BADGER_ID' found!");
			console.error("If you recently set this environment variable, you may need to restart for this to take effect.");
		}
		return bid;
	}


    return {
        setBadgerId: setBadgerId,
        getBadgerId: getBadgerId,
    }
}

const CS571 = CreateCS571();

export default CS571;